import React, { useEffect } from "react";

export function Tabs({ value, onValueChange, children, className }) {
  useEffect(() => {
    const handler = (e) => onValueChange(e.detail);
    window.addEventListener("tabChange", handler);
    return () => window.removeEventListener("tabChange", handler);
  }, [onValueChange]);

  return <div className={className}>{children}</div>;
}

export function TabsList({ children, className }) {
  return <div className={className}>{children}</div>;
}

export function TabsTrigger({ children, value }) {
  return (
    <button
      className="px-3 py-2 bg-white border border-orange-300 hover:bg-orange-100 rounded text-sm font-semibold"
      onClick={() => window.dispatchEvent(new CustomEvent("tabChange", { detail: value }))}
    >
      {children}
    </button>
  );
}

export function TabsContent({ children, value }) {
  const current = document.querySelector('[data-tab-selected]')?.getAttribute('data-tab-selected');
  return value === current ? <div>{children}</div> : null;
}
