import React, { createContext, useContext } from "react";

// Create a context for the active tab
const TabsContext = createContext();

export function Tabs({ value, onValueChange, children, className }) {
  return (
    <TabsContext.Provider value={{ value, onValueChange }}>
      <div className={className}>{children}</div>
    </TabsContext.Provider>
  );
}

export function TabsList({ children, className }) {
  return <div className={className}>{children}</div>;
}

export function TabsTrigger({ children, value }) {
  const { value: activeValue, onValueChange } = useContext(TabsContext);

  const typeColors = {
    1: "bg-orange-100 border-orange-300 text-orange-700",
    2: "bg-pink-100 border-pink-300 text-pink-700",
    3: "bg-yellow-100 border-yellow-300 text-yellow-700",
    4: "bg-purple-100 border-purple-300 text-purple-700",
    5: "bg-blue-100 border-blue-300 text-blue-700",
    6: "bg-teal-100 border-teal-300 text-teal-700",
    7: "bg-lime-100 border-lime-300 text-lime-700",
    8: "bg-red-100 border-red-300 text-red-700",
    9: "bg-green-100 border-green-300 text-green-700"
  };

  const baseClasses = "px-3 py-2 border rounded text-sm font-semibold transition-colors";
  const typeNum = Number(value);
  const isActive = value === activeValue;
  const activeClass = typeColors[typeNum] || "bg-gray-200 border-gray-300 text-gray-700";
  const inactiveClass = "bg-white border-gray-200 hover:bg-gray-50 text-gray-600";

  return (
    <button
      className={`${baseClasses} ${isActive ? activeClass : inactiveClass}`}
      onClick={() => onValueChange(value)}
    >
      {children}
    </button>
  );
}

export function TabsContent({ children, value }) {
  const { value: activeValue } = useContext(TabsContext);
  return value === activeValue ? <div>{children}</div> : null;
}
